mapper.select:
SELECT 

FROM iim_chat_history a

WHERE a.id = #{id}

SELECT 

FROM iim_chat_history a




		AND a.userid1 = #{userid1}


		AND a.userid2 = #{userid2}


		AND a.msg LIKE 
			'%'||#{msg}||'%'
			'%'+#{msg}+'%'
			concat('%',#{msg},'%')


		AND a.status = #{status}


		AND a.create_date = #{createDate}


		ORDER BY a.create_date asc
