mapper.select:
SELECT 

FROM iim_mail_box a

WHERE a.id = #{id}

SELECT 

FROM iim_mail_box a



		AND a.receiverid = #{receiver.id}


		AND a.readstatus = #{readstatus}


		AND ( mail.title like 
			'%'||#{mail.title}||'%'
			CONCAT('%', #{mail.title}, '%')
			or 
			mail.content like
			'%'||#{mail.title}||'%'
			CONCAT('%', #{mail.title}, '%')
			)




		ORDER BY ${page.orderBy}

SELECT 

FROM iim_mail_box a






		ORDER BY ${page.orderBy}

SELECT count(*)
FROM iim_mail_box a



		AND a.receiverid = #{receiver.id}



		and a.readstatus = #{readstatus}

mapper.update:
DELETE FROM iim_mail_box


		WHERE id = #{id}


		WHERE mailid = #{mail.id}
