mapper.update:
UPDATE test_data_child2 SET 	
	start = #{start.id},
	end = #{end.id},
	price = #{price},
	test_data_main = #{testDataMain.id},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

DELETE FROM test_data_child2


		WHERE id = #{id}


		WHERE test_data_main = #{testDataMain.id}

UPDATE test_data_child2 SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE test_data_main = #{testDataMain.id}

mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	test_data_child2 a

	1=1
	and a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.start = #{start.id}


		AND a.end = #{end.id}


		AND a.price = #{price}


		AND a.test_data_main = #{testDataMain.id}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}


		ORDER BY a.create_date ASC
