mapper.select:
SELECT 

FROM test_data_main a

WHERE a.id = #{id}

SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{user.id}


		AND a.office_id = #{office.id}


		AND a.area_id = #{area.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}


		AND a.in_date BETWEEN #{beginInDate} AND #{endInDate}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM test_data_main a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test_data_main SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	test_data_main a

	1=1
	and a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{user.id}


		AND a.office_id = #{office.id}


		AND a.area_id = #{area.id}


		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}


		AND a.in_date BETWEEN #{beginInDate} AND #{endInDate}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}


		ORDER BY a.update_date DESC
