mapper.select:
SELECT 
	* 
FROM sys_dict 
WHERE del_flag = #{DEL_FLAG_NORMAL}

	AND id = #{id}


	AND type = #{type}


	AND value in (#{value})


	AND description LIKE 
			'%'||#{description}||'%'
			CONCAT('%', #{description}, '%')



		ORDER BY ${page.orderBy}


		ORDER BY update_date DESC

mapper.update:
UPDATE sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
