mapper.select:
SELECT 

FROM test_interface a

WHERE a.id = #{id}

SELECT 

FROM test_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}




		ORDER BY ${page.orderBy}

SELECT 

FROM test_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE test_interface SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
