mapper.select:
SELECT 

FROM sys_job a

WHERE a.job_id = #{id}

SELECT 

FROM sys_job a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.job_id = #{id}


		AND a.job_name LIKE 
			'%'||#{jobName}||'%'
			'%'+#{jobName}+'%'
			concat('%',#{jobName},'%')


		AND a.job_group = #{jobGroup}


		AND a.method_name LIKE 
			'%'||#{methodName}||'%'
			'%'+#{methodName}+'%'
			concat('%',#{methodName},'%')


		AND a.method_params = #{methodParams}


		AND a.cron_expression = #{cronExpression}


		AND a.misfire_policy = #{misfirePolicy}


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}


		AND a.remark LIKE 
			'%'||#{remark}||'%'
			'%'+#{remark}+'%'
			concat('%',#{remark},'%')


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_job a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE job_id = #{id}

mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	sys_job a

	1=1
	and a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.job_id = #{id}


		AND a.job_name LIKE
			'%'||#{jobName}||'%'
			'%'+#{jobName}+'%'
			concat('%',#{jobName},'%')


		AND a.job_group = #{jobGroup}


		AND a.method_name LIKE
			'%'||#{methodName}||'%'
			'%'+#{methodName}+'%'
			concat('%',#{methodName},'%')


		AND a.method_params = #{methodParams}


		AND a.cron_expression = #{cronExpression}


		AND a.misfire_policy = #{misfirePolicy}


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}


		AND a.remark LIKE
			'%'||#{remark}||'%'
			'%'+#{remark}+'%'
			concat('%',#{remark},'%')


		AND a.del_flag = #{delFlag}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}


		ORDER BY a.update_date DESC
