mapper.select:
SELECT 

FROM sys_server a

WHERE a.id = #{id}

SELECT 

FROM sys_server a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.server_number = #{serverNumber}


		AND a.server_address = #{serverAddress}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.label = #{label}


		AND a.picture = #{picture}


		AND a.type = #{type}


		AND a.sort = #{sort}


		AND a.description = #{description}


		AND a.remarks LIKE 
			'%'||#{remarks}||'%'
			'%'+#{remarks}+'%'
			concat('%',#{remarks},'%')


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_server a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_server SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT


			DATE_FORMAT(a.create_date,'${totalType}') as totalDate,




	count(*) as totalCount
FROM
	sys_server a

	1=1
	and a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.server_number = #{serverNumber}


		AND a.server_address = #{serverAddress}


		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.label = #{label}


		AND a.picture = #{picture}


		AND a.type = #{type}


		AND a.sort = #{sort}


		AND a.description = #{description}


		AND a.remarks LIKE
			'%'||#{remarks}||'%'
			'%'+#{remarks}+'%'
			concat('%',#{remarks},'%')


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date BETWEEN #{beginUpdateDate} AND #{endUpdateDate}


		AND a.del_flag = #{delFlag}




		GROUP BY DATE_FORMAT(a.create_date,'${totalType}'),${groupBy}


		GROUP BY ${groupBy}


		GROUP BY DATE_FORMAT(a.create_date,'${totalType}')






		ORDER BY ${orderBy}


		ORDER BY a.update_date DESC
