mapper.select:
SELECT 

FROM sys_config a

WHERE a.id = #{id}

SELECT 

FROM sys_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.type LIKE
		'%'||#{type}||'%'
		'%'+#{type}+'%'
		concat('%',#{type},'%')


		AND a.value = #{value}


		AND a.label = #{label}


		AND a.description LIKE 
			'%'||#{description}||'%'
			'%'+#{description}+'%'
			concat('%',#{description},'%')


		AND a.sort = #{sort}


		AND a.create_by = #{createBy.id}


		AND a.update_by = #{updateBy.id}


		AND a.del_flag = #{delFlag}


		AND a.picture = #{picture}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
